class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=t.newCommentDom(n.data.comment);$(`#post-comments-${e}`).prepend(o),$("#comment-input").val(""),t.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`<div class="comment" id="comment-${e._id}">\n        <div class="name">\n            <span>\n            ${e.user.avatar?`<img src="${e.user.avatar}" alt="${e.user.name}" >`:`<img src="/images/default-avatar.png" alt="${e.user.name}" >`}\n            ${e.user.name}</span>\n            <div>\n            <div class="dropdown">\n                <button class="dropbtn"><i class="fa-solid fa-circle-chevron-down"></i></button>\n                <div class="dropdown-content"><a class="delete-comment-button" href="/comments/destroy/${e._id}">Delete</a></div>\n            </div>\n            </div>\n        </div>\n        <div class="content">\n            ${e.content}\n        </div>\n        <small>\n               <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${e._id}&type=Comment">\n               <button class="like__btn">\n               <span id="icon"><i class="far fa-thumbs-up"></i></span>\n               <span id="count">\n                   0\n               </span> Like\n           </button>\n                </a>\n                            \n        </small>\n        </div>`)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment_id}`).remove(),console.log(`#comment-${e.data.comment_id}`),new Noty({theme:"relax",text:"Comment Deleted !!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}