class PostComments{constructor(t){this.postId=t,this.postContainer=$(`#post-${t}`),this.newCommentForm=$(`#post-${t}-comments-form`),this.createComment(t);let e=this;$(" .delete-comment-button",this.postContainer).each((function(){e.deleteComment($(this))}))}createComment(t){let e=this;this.newCommentForm.submit((function(n){n.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(n){let o=e.newCommentDom(n.data.comment);$(`#post-comments-${t}`).prepend(o),$("#comment-input").val(""),e.deleteComment($(" .delete-comment-button",o)),new ToggleLike($(" .toggle-like-button",o)),new Noty({theme:"relax",text:"Comment published!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(t){console.log(t.responseText)}})}))}newCommentDom(t){return $(`<div class="comment" id="comment-${t._id}">\n        <div class="name">\n            <span>\n            ${t.user.avatar?`<img src="${t.user.avatar}" alt="${t.user.name}" >`:`<img src="${assetPath("images/default-avatar.png")}" alt="${t.user.name}" >`}\n            ${t.user.name}</span>\n            <div>\n            <div class="dropdown">\n                <button class="dropbtn"><i class="fa-solid fa-circle-chevron-down"></i></button>\n                <div class="dropdown-content"><a class="delete-comment-button" href="/comments/destroy/${t._id}">Delete</a></div>\n            </div>\n            </div>\n        </div>\n        <div class="content">\n            ${t.content}\n        </div>\n        <small>\n               <a class="toggle-like-button" data-likes="0" href="/likes/toggle/?id=${t._id}&type=Comment">\n               <button class="like__btn">\n               <span id="icon"><i class="far fa-thumbs-up"></i></span>\n               <span id="count">\n                   0\n               </span> Like\n           </button>\n                </a>\n                            \n        </small>\n        </div>`)}deleteComment(t){$(t).click((function(e){e.preventDefault(),$.ajax({type:"get",url:$(t).prop("href"),success:function(t){$(`#comment-${t.data.comment_id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted !!",type:"success",layout:"topRight",timeout:1500}).show()},error:function(t){console.log(t.responseText)}})}))}}