function NewsApi(){$.ajax({url:"https://newsapi.org/v2/top-headlines?country=in&apiKey=510a27dd6ab14b5188faf8feb46981f5",method:"GET",dataType:"json",success:function(e){let n=e.articles,l=document.querySelectorAll(".news-list-container"),t="";n=n.slice(0,7);for(let e=0;e<n.length;e++)t+=`<li class="news mb-1">\n        <a href="${n[e].url}">${n[e].title} <i class="fa fa-external-link"></i></a>\n        </li>`;for(let e=0;e<l.length;e++)l[e].innerHTML=t},error:function(e){console.log(e)}})}NewsApi();