class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let n=parseInt($(e).attr("data-likes"));console.log(n),1==t.data.deleted?(n-=1,$(e).attr("data-likes",n)):(n+=1,$(e).attr("data-likes",n)),$(e).html(`<button class="like__btn">\n                <span id="icon"><i class="fas fa-thumbs-up"></i></span>\n                <span id="count">\n                    ${n}\n                </span> Like\n            </button>`)})).fail((function(t){console.log("error in completing the request")}))}))}}